// SPDX-License-Identifier: MIT
pragma solidity >0.8.0 <0.9.0;

abstract contract Signature {
    function getMessageHash(string memory _message)
        internal
        pure
        returns (bytes32)
    {
        return keccak256(abi.encodePacked(_message));
    }

    function getEthSignedMessageHash(bytes32 _messageHash)
        internal
        pure
        returns (bytes32)
    {
        return
            keccak256(
                abi.encodePacked(
                    "\x19Ethereum Signed Message:\n32",
                    _messageHash
                )
            );
    }

    function recoverSigner(
        bytes32 _ethSignedMessageHash,
        bytes memory _signature
    ) public pure returns (address) {
        (bytes32 r, bytes32 s, uint8 v) = splitSignature(_signature);

        return ecrecover(_ethSignedMessageHash, v, r, s);
    }

    function splitSignature(bytes memory _signature)
        public
        pure
        returns (
            bytes32 r,
            bytes32 s,
            uint8 v
        )
    {
        require(_signature.length == 65, "invalid signature length");
        assembly {
            // first 32 bytes, after the length prefix
            r := mload(add(_signature, 32))
            // second 32 bytes
            s := mload(add(_signature, 64))
            // final byte (first byte of the next 32 bytes)
            v := byte(0, mload(add(_signature, 96)))
        }
    }

    function verify(
        address _signer,
        string memory _message,
        bytes memory _signature
    ) external pure returns (bool) {
        //messageHash
        bytes32 messageHash = getMessageHash(_message);

        // ethMessageHash
        bytes32 ethSignedMessageHash = getEthSignedMessageHash(messageHash);

        //recover
        return recoverSigner(ethSignedMessageHash, _signature) == _signer;
    }

    function isValidSignature(
        address _signer,
        bytes32 _ethSignedMessageHash,
        bytes memory _signature
    ) internal pure returns (bool) {
        return recoverSigner(_ethSignedMessageHash, _signature) == _signer;
    }

    modifier validSignature(
        address _signer,
        bytes32 _messageHash,
        bytes memory _signature
    ) {
        require(
            isValidSignature(_signer, _messageHash, _signature),
            "The signature invalid"
        );
        _;
    }
}
